import flaskimport numpy as npimport picklefrom flask import requestapp = flask.Flask(__name__)app.config["DEBUG"] = True@app.route('/api/v1/predictor', methods=['GET'])def home():    if 'age' in request.args:        age = int(request.args['age'])    else:        return "Error: No age field provided. Please specify."    if 'n_eggs' in request.args:        n_eggs = int(request.args['n_eggs'])    else:        return "Error: number of eggs not provided. Please specify."    if 'rank' in request.args:        rank = int(request.args['rank'])    else:        return "Error: number of attempts at ivf not provided. Please specify."    pat = np.asarray([[age, n_eggs, rank]])    filename = 'RFC1opt.sav'    loadRFC1 = pickle.load(open(filename, 'rb'))    prediction = loadRFC1.predict_proba(pat)[0][1]    if (age not in range(18, 50)) | (n_eggs not in range(1, 25)) | (rank not in range(1, 13)):        return 'The age, number of eggs, or number of attempts you have entered is invalid'    else:        return f"<h1>Open IVF fertility predictor</h1><p>At age {age} and with {n_eggs} eggs collected on your {rank}. attempt at IVF " \               f"you have {round(prediction*100)}% chance.</p> "if __name__ == '__main__':    app.run()