import flaskimport numpy as npimport picklefrom flask import requestimport jsonapp = flask.Flask(__name__)app.config["DEBUG"] = True@app.route('/api/v1/predictor', methods=['GET'])def home():    if 'age' in request.args:        age = int(request.args['age'])    else:        age_error = {             "success": False,            "error_code": 1        }        return json.dumps(age_error)#     "Error: No age field provided. Please specify."    if 'n_eggs' in request.args:        n_eggs = int(request.args['n_eggs'])    else:        egg_error = {             "success": False,            "error_code": 2        }        return json.dumps(egg_error)#     "Error: number of eggs not provided. Please specify."    if 'rank' in request.args:        rank = int(request.args['rank'])    else:        rank_error = {             "success": False,            "error_code": 3        }        return json.dumps(rank_error)#     "Error: number of attempts at ivf not provided. Please specify."    if (age not in range(18, 50)) | (n_eggs not in range(1, 25)) | (rank not in range(1, 13)):        range_error = {             "success": False,            "error_code": 4        }        return json.dumps(range_error)#     'The age, number of eggs, or number of attempts you have entered is invalid'    else:        pat = np.asarray([[age, n_eggs, rank]])        filename = 'RFC1opt.sav'        loadRFC1 = pickle.load(open(filename, 'rb'))        prediction = loadRFC1.predict_proba(pat)[0][1]        json_pred = {             "success": True,            "data":round(prediction*100)        }        answer = json.dumps(json_pred)        return answerif __name__ == '__main__':    app.run()